messages-tag-count.map:
function (doc) {
  if (doc.message) {
    function emitTag(tag) {
        emit(tag);
    }
    doc.tags.forEach(emitTag);
    if (doc.tags.length == 0) {
      emit("untagged");
    }
  }
}

