totals.map:
function (doc) {
  if (doc.message) {
    emit("messages");
  }
  if (doc.logbook) {
    emit("logs");
  }
  if (doc.productivity && doc.type == "task") {
    emit("tasks");
  }
}

task-priority-count.map:
function (doc) {
  if (doc.productivity && doc.type == "task" && doc.status != 'done' && doc.status != 'cancelled') {
    emit(doc.priority);
  }
}

logs-start-months.map:
function (doc) {
  if (doc.logbook) {
    var d = doc.start.slice(0,4);
    var m = doc.start.slice(5,7);
    var x = d + '-' + m;
    emit(x);
  }
}

logs-start-years.map:
function (doc) {
  if (doc.logbook) {
    var d = doc.start.slice(0,4);
    emit(d);
  }
}

logs-start-days.map:
function (doc) {
  if (doc.logbook) {
    var y = doc.start.slice(0,4);
    var m = doc.start.slice(5,7);
    var d = doc.start.slice(8,10);
    var x = [y, m, d].join('-');
    emit(x);
  }
}

tasks-tag-count.map:
function (doc) {
  if (doc.productivity && doc.type == "task") {
    function emitTag(tag) {
        emit(tag);
    }
    doc.tags.forEach(emitTag);
    if (doc.tags.length == 0) {
      emit("untagged");
    }
  }
}

tasks-due.map:
function (doc) {
  if (doc.productivity 
      && doc.due 
      && doc.type === "task" 
      && doc.status != 'done' 
      && doc.status != 'cancelled') {
    emit(doc.due);
  }
}
